import React, { useState } from 'react';
import './Programs.css'

const Programs = () => {
    const [exerciseType, setExerciseType] = useState('cardio');
    const [difficultyLevel, setDifficultyLevel] = useState('beginner');
    const [workoutPlan, setWorkoutPlan] = useState(null);

    const handleExerciseTypeChange = (event) => {
      setExerciseType(event.target.value);
    };

    const handleDifficultyLevelChange = (event) => {
      setDifficultyLevel(event.target.value);
    };

    const searchWorkoutPlan = () => {
      const requestData = {
        exercise_type: exerciseType,
        difficulty_level: difficultyLevel,
      };

      fetch('/api/workout', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(requestData),
      })
        .then((response) => {
          if (!response.ok) {
            throw new Error('Network response was not ok');
          }
          return response.json();
        })
        .then((data) => {
          setWorkoutPlan(data);
        })
        .catch((error) => {
          console.error('Error:', error);
          setWorkoutPlan(null);
        });
    };

    return (
      <div>
        <h1>Workout Plan Generator</h1>
        <form>
          <label htmlFor="exerciseType">Exercise Type:</label>
          <select id="exerciseType" value={exerciseType} onChange={handleExerciseTypeChange}>
            <option value="cardio">Cardio</option>
            <option value="strength">Strength Training</option>
            {/* Add more options as needed */}
          </select>
          <label htmlFor="difficultyLevel">Difficulty Level:</label>
          <select id="difficultyLevel" value={difficultyLevel} onChange={handleDifficultyLevelChange}>
            <option value="beginner">Beginner</option>
            <option value="intermediate">Intermediate</option>
            <option value="advanced">Advanced</option>
            {/* Add more options as needed */}
          </select>
          <button type="button" onClick={searchWorkoutPlan}>
            Search
          </button>
        </form>
        {workoutPlan && (
          <div>
            <h2>Your Workout Plan:</h2>
            {/* Render the workout plan data here */}
            <pre>{JSON.stringify(workoutPlan, null, 2)}</pre>
          </div>
        )}
      </div>
    );
  };

  export default Programs;
