import React, { useState } from 'react';
import './WorkoutPlan.css'

const WorkoutPlan = () => {
    const [equipmentType, setEquipmentType] = useState('Bands');
    const [difficultyLevel, setDifficultyLevel] = useState('Beginner');
    const [workoutPlan, setWorkoutPlan] = useState(null);

    const handleEquipmentTypeChange = (event) => {
      setEquipmentType(event.target.value);
    };

    const handleDifficultyLevelChange = (event) => {
      setDifficultyLevel(event.target.value);
    };

    const searchWorkoutPlan = () => {
      const requestData = {
        equipment_type: equipmentType,
        difficulty_level: difficultyLevel,
      };

      fetch('http://127.0.0.1:5000/api/workout', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(requestData),
      })
        .then((response) => {
          if (!response.ok) {
            throw new Error('Network response was not ok');
          }
          return response.json();
        })
        .then((data) => {
          setWorkoutPlan(data);
        })
        .catch((error) => {
          console.error('Error:', error);
          setWorkoutPlan(null);
        });
    };

    return (
      <div>
        <div className='programs-header'>
        <h1 className='Header'>
          Or search it yourself
          </h1>
        </div>
        <form>
        <label for="equipment_type">Equipment Type:</label>
          <select id="equipment_type" value={equipmentType} onChange={handleEquipmentTypeChange}>
            <option value="Bands">Bands</option>
            <option value="strength">"Strength Training"</option>
            {/* Add more options as needed */}
          </select>
          <label htmlFor="difficultyLevel">Difficulty Level:</label>
          <select id="difficultyLevel" value={difficultyLevel} onChange={handleDifficultyLevelChange}>
            <option value="Beginner">Beginner</option>
            <option value="Intermediate">Intermediate</option>
            {/* Add more options as needed */}
          </select>
          <button type="button" onClick={searchWorkoutPlan}>
            Search
          </button>
        </form>
        {workoutPlan && (
          <div>
            <h2>Workouts based on your search</h2>
            {/* Render the workout plan data here */}
            <pre>{JSON.stringify(workoutPlan, null, 2)}</pre>
            console.log(JSON.stringify(workoutPlan, null, 2))
          </div>
        )}
      </div>
    );
  };

  export default WorkoutPlan;
